const formData = context.component?.variables?.['form_data_var'];
if (!formData.name || formData.name.length < 3) {
  return; // Name validation failed
}

if (formData.price < 0 || formData.price > 1000) {
  return; // Price validation failed
}

// Set submitting state
context.component.variables['is_submitting_var'] = true;

// Get current vehicles data
const vehicles = [...variables['8089a760-b021-4c5a-9cbd-7a55b4ac6572']];
const mode = context.component?.props?.['mode_arg'];
const vehicleData = context.component?.props?.['vehicle_data_arg'];

// Simulate API request delay
setTimeout(async () => {
  if (mode === 'create') {
    // Generate a unique ID for the new vehicle
    const newId = 'v' + (Math.floor(Math.random() * 10000) + 1);

    // Add new vehicle
    vehicles.push({
      id: newId,
      name: formData.name,
      image: formData.image || 'https://images.unsplash.com/photo-1533473359331-0135ef1b58bf?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1470&q=80',
      price: Number(formData.price),
      status: formData.status,
      category: formData.category
    });
  } else if (mode === 'edit' && vehicleData) {
    // Update existing vehicle
    const index = vehicles.findIndex(v => v.id === vehicleData.id);
    if (index !== -1) {
      vehicles[index] = {
        ...vehicles[index],
        name: formData.name,
        image: formData.image || vehicles[index].image,
        price: Number(formData.price),
        status: formData.status,
        category: formData.category
      };
    }
  }

  // Update global vehicles data
  variables['8089a760-b021-4c5a-9cbd-7a55b4ac6572'] = vehicles;

  // Reset submitting state and close popup
  context.component.variables['is_submitting_var'] = false;
  await utilsFunctions.closePopup(context);
}, 800);

