// Get vehicles data and filters
  const vehicles = variables['8089a760-b021-4c5a-9cbd-7a55b4ac6572'] || [];
  const filters = variables['7aebed04-87b6-4c0b-bba9-d78abe388770'] || {
    search: '',
    status: 'all',
    category: 'all',
    priceRange: [0, 500]
  };

  // Get sort value from select
  const sortSelect = variables['sort_select-value'] || 'name_asc';

  // Filter vehicles
  let filtered = [...vehicles];

  // Filter by search term
  if (filters.search && filters.search.trim() !== '') {
    const searchTerm = filters.search.toLowerCase().trim();
    filtered = filtered.filter(vehicle => 
      vehicle.name.toLowerCase().includes(searchTerm)
    );
  }

  // Filter by status
  if (filters.status && filters.status !== 'all') {
    filtered = filtered.filter(vehicle => 
      vehicle.status === filters.status
    );
  }

  // Filter by category
  if (filters.category && filters.category !== 'all') {
    filtered = filtered.filter(vehicle => 
      vehicle.category === filters.category
    );
  }

  // Filter by price range
  if (filters.priceRange && Array.isArray(filters.priceRange) && filters.priceRange.length === 2) {
    const [minPrice, maxPrice] = filters.priceRange;
    filtered = filtered.filter(vehicle => 
      vehicle.price >= minPrice && vehicle.price <= maxPrice
    );
  }

  // Sort vehicles
  switch (sortSelect) {
    case 'name_asc':
      filtered.sort((a, b) => a.name.localeCompare(b.name));
      break;
    case 'name_desc':
      filtered.sort((a, b) => b.name.localeCompare(a.name));
      break;
    case 'price_asc':
      filtered.sort((a, b) => a.price - b.price);
      break;
    case 'price_desc':
      filtered.sort((a, b) => b.price - a.price);
      break;
    default:
      filtered.sort((a, b) => a.name.localeCompare(b.name));
  }

  return filtered;
