  name: "",
  email: "",
  phone: "",
  membershipStatus: "",
  notes: ""
};

// If we have a customerId, find the customer data
if (customerId) {
  const customersData = variables['customersData'] || [];
  const customer = customersData.find(c => c.id === customerId);

  if (customer) {
    // Populate the form with customer data
    formData = {
      name: customer.name || "",
      email: customer.email || "",
      phone: customer.phone || "",
      membershipStatus: customer.membershipStatus || "",
      notes: customer.notes || ""
    };
  }
}

// Set the form data in the popup's internal variable
context.component.variables['customerForm'] = formData;

// Set the editing state
