// Get current vehicles data
  const vehicles = [...variables['0a53135a-fdf0-4b15-8a12-b5502145bc77']];

  // Update each vehicle with simulated changes
  vehicles.forEach(vehicle => {
    // Randomly update speed (10 km/h with constraints)
    const speedChange = Math.floor(Math.random() * 21) - 10;
    vehicle.speed = Math.max(0, Math.min(120, vehicle.speed + speedChange));

    // Update status based on speed
    if (vehicle.speed === 0) {
      vehicle.status = Math.random() > 0.5 ? 'Parked' : 'Stopped';
    } else if (vehicle.speed < 5) {
      vehicle.status = 'Idle';
    } else {
      vehicle.status = 'On Trip';
    }

    // Increment trip distance if moving
    if (vehicle.speed > 0) {
      // Convert km/h to km per 15 seconds
      const distanceIncrement = (vehicle.speed / 3600) * 15;
      vehicle.tripKms += distanceIncrement;
    }

    // Decrease fuel level slightly if moving
    if (vehicle.speed > 0) {
      const fuelDecrease = Math.random() * 0.5;
      vehicle.fuelLevel = Math.max(0, vehicle.fuelLevel - fuelDecrease);
    }

    // Simulate location change if moving
    if (vehicle.speed > 0) {
      // Approximate 15 seconds of movement in degrees
      const latChange = ((Math.random() * 2) - 1) * 0.0005 * (vehicle.speed / 50);
      const lngChange = ((Math.random() * 2) - 1) * 0.0005 * (vehicle.speed / 50);

      vehicle.location = {
        lat: vehicle.location.lat + latChange,
        lng: vehicle.location.lng + lngChange
      };
    }

    // Randomly change tire pressure (small chance)
    if (Math.random() < 0.05) {
      const tires = ['fl', 'fr', 'bl', 'br'];
      const randomTire = tires[Math.floor(Math.random() * tires.length)];
      vehicle.tires[randomTire] = vehicle.tires[randomTire] === 'Normal' ? 'Low' : 'Normal';
    }
  });

  // Update the global variable with the modified vehicles
  variables['0a53135a-fdf0-4b15-8a12-b5502145bc77'] = vehicles;

  return vehicles;
