const updateVehicleData = () => {
    // Get current vehicles data
    const vehicles = [...variables["dad02651-635e-403a-875e-9a07253e1c72"]];

    // Update each vehicle with random changes to simulate live data
    const updatedVehicles = vehicles.map(vehicle => {
        // Clone the vehicle to avoid reference issues
        const updatedVehicle = JSON.parse(JSON.stringify(vehicle));

        // Randomly update location (small changes to simulate movement)
        const latChange = (Math.random() - 0.5) * 0.001;
        const lngChange = (Math.random() - 0.5) * 0.001;
        updatedVehicle.location.lat += latChange;
        updatedVehicle.location.lng += lngChange;

        // Update speed based on status
        if (updatedVehicle.status === 'On Trip') {
            // Random speed between 40-80 km/h for vehicles on trip
            updatedVehicle.speed = Math.floor(40 + Math.random() * 40);
            // Increase trip KMs
            updatedVehicle.tripKms += (updatedVehicle.speed / 3600) * 15; // 15 seconds in hours * speed
        } else if (updatedVehicle.status === 'Parked') {
            updatedVehicle.speed = 0;
        } else if (updatedVehicle.status === 'Idle') {
            updatedVehicle.speed = Math.floor(Math.random() * 5);
        } else {
            updatedVehicle.speed = 0;
        }

        // Randomly decrease fuel level for vehicles on trip
        if (updatedVehicle.status === 'On Trip') {
            updatedVehicle.fuelLevel = Math.max(0, updatedVehicle.fuelLevel - Math.random() * 0.5);
        }

        // Randomly change tire pressure status (very low probability)
        if (Math.random() < 0.05) {
            const tires = ['fl', 'fr', 'bl', 'br'];
            const randomTire = tires[Math.floor(Math.random() * tires.length)];
            updatedVehicle.tires[randomTire] = updatedVehicle.tires[randomTire] === 'Normal' ? 'Low' : 'Normal';
        }

        // Randomly change vehicle status (very low probability)
        if (Math.random() < 0.03) {
            const statuses = ['On Trip', 'Parked', 'Idle', 'Stopped'];
            const currentIndex = statuses.indexOf(updatedVehicle.status);
            const newIndex = (currentIndex + 1) % statuses.length;
            updatedVehicle.status = statuses[newIndex];
        }

        return updatedVehicle;
    });

    // Update the global variable with new data
    variables["dad02651-635e-403a-875e-9a07253e1c72"] = updatedVehicles;

    // If a vehicle is selected in the popup, update its details too
    if (variables["d1cc0fe7-737a-48bd-a87e-d904d63e49ef"]) {
        const selectedVehicleId = variables["d1cc0fe7-737a-48bd-a87e-d904d63e49ef"].id;
        const updatedSelectedVehicle = updatedVehicles.find(v => v.id === selectedVehicleId);
        if (updatedSelectedVehicle) {
            variables["d1cc0fe7-737a-48bd-a87e-d904d63e49ef"] = updatedSelectedVehicle;
        }
    }
};

