// Get current vehicles data
  const vehicles = [...variables['2edc2c0c-bace-4951-bd58-8fbdb8f66516']];

  // Update each vehicle with simulated changes
  const updatedVehicles = vehicles.map(vehicle => {
    const updatedVehicle = { ...vehicle };

    // Randomly update speed (±5 km/h)
    const speedChange = Math.floor(Math.random() * 11) - 5;
    updatedVehicle.speed = Math.max(0, updatedVehicle.speed + speedChange);

    // Update trip distance based on speed
    if (updatedVehicle.status === 'On Trip') {
      // Convert km/h to km per 15 seconds
      const distanceIncrement = (updatedVehicle.speed / 3600) * 15;
      updatedVehicle.tripKms = parseFloat((updatedVehicle.tripKms + distanceIncrement).toFixed(1));
    }

    // Randomly update fuel level (decrease by 0-1%)
    if (updatedVehicle.status === 'On Trip') {
      const fuelDecrease = Math.random() < 0.7 ? 1 : 0;
      updatedVehicle.fuelLevel = Math.max(0, updatedVehicle.fuelLevel - fuelDecrease);
    }

    // Randomly change tire status (5% chance for any tire)
    if (Math.random() < 0.05) {
      const tires = ['fl', 'fr', 'bl', 'br'];
      const randomTire = tires[Math.floor(Math.random() * tires.length)];
      updatedVehicle.tires[randomTire] = updatedVehicle.tires[randomTire] === 'Normal' ? 'Low' : 'Normal';
    }

    // Randomly update location slightly
    if (updatedVehicle.location) {
      const latChange = (Math.random() - 0.5) * 0.001;
      const lngChange = (Math.random() - 0.5) * 0.001;
      updatedVehicle.location.lat += latChange;
      updatedVehicle.location.lng += lngChange;
    }

    return updatedVehicle;
  });

  // Update the global variable with new data
  variables['2edc2c0c-bace-4951-bd58-8fbdb8f66516'] = updatedVehicles;

  // Show refresh indicator briefly
  variables['isRefreshing'] = true;
  setTimeout(() => {
    variables['isRefreshing'] = false;
  }, 1000);

  return updatedVehicles;
